<!doctype html><html lang="en"><head>  <meta charset="utf-8">  <title>The HTML5 Herald</title>  <meta name="description" content="The HTML5 Herald">  <meta name="author" content="SitePoint">  <link rel="stylesheet" href="css/styles.css?v=1.0">  <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.1.1/jquery.min.js"></script>  <script>	$(document).ready(function(){		$(document).mousemove(function(event){			$("span").text(event.pageX + ", " + event.pageY);			$( "RestartTimer" ).load( "classes/RestartTimer.php" );		});	});  </script></head><body id="body">	<h2><div id="RestartTimer"></div></h2>	<span></span>  <script src="js/scripts.js"></script></body></html>